import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import EnglishAuction from '../artifacts/contracts/Auction.sol/EnglishAuction.json';
import './AuctionDapp.css';

const contractAbi = [
  EnglishAuction
];
const contractAddress = "0x437cb27e2407a5f90920ba661d5ed7d8d58c6d38"; // Replace with the deployed contract address

const App = () => {
  // State variables
  const [contract, setContract] = useState(null);
  const [highestBid, setHighestBid] = useState(0);
  const [bidAmount, setBidAmount] = useState(0);
  const [account, setAccount] = useState(null);

  // useEffect to initialize the contract and get user account
  useEffect(() => {
    initContract();
    getAccount();
  }, []);

  // Function to initialize the contract
  const initContract = async () => {
    try {
      // Check if MetaMask is installed
      if (typeof window.ethereum !== "undefined") {
        // Connect to MetaMask
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();

        // Load the contract
        const contract = new ethers.Contract(contractAddress, contractAbi, signer);
        setContract(contract);

        // Get the current highest bid
        const currentBid = await contract.highestBid();
        setHighestBid(currentBid);
      } else {
        console.log("MetaMask not detected.");
      }
    } catch (error) {
      console.error("Error initializing contract:", error);
    }
  };

  // Function to get the user's account
  const getAccount = async () => {
    try {
      if (typeof window.ethereum !== "undefined") {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const accounts = await provider.listAccounts();
        setAccount(accounts[0]);
      }
    } catch (error) {
      console.error("Error fetching account:", error);
    }
  };

  // Function to place a bid
  const placeBid = async () => {
    try {
      if (contract && bidAmount > highestBid && account) {
        const transaction = await contract.bid({
          value: ethers.utils.parseEther(bidAmount.toString()),
        });
        await transaction.wait(); // Wait for the transaction to be mined
        const currentBid = await contract.highestBid();
        setHighestBid(currentBid);
        setBidAmount(0); // Clear the bid input field
      }
    } catch (error) {
      console.error("Error placing bid:", error);
    }
  };

  return (
    <div>
      <h1>English Auction</h1>
      <p>Highest Bid: {ethers.utils.formatEther(highestBid)} ETH</p>
      <input
        type="number"
        placeholder="Enter Bid Amount (ETH)"
        value={bidAmount}
        onChange={(e) => setBidAmount(e.target.value)}
      />
      <button onClick={placeBid}>Place Bid</button>
    </div>
  );
};

export default App;
