import React, { useEffect, useState } from "react";
import { ethers } from "ethers";
import EnglishAuction from '../artifacts/contracts/Auction.sol/EnglishAuction.json';


const AuctionApp = () => {
  // Contract address and provider (replace with your actual address)
  const auctionContractAddress = "0xeb712ab7053c1033dff4e333bae35b0bd002850b";
  const provider = new ethers.providers.Web3Provider(window.ethereum);
  const contractABI = EnglishAuction.abi;

  // State variables
  const [auctions, setAuctions] = useState([]);
  const [selectedAuction, setSelectedAuction] = useState(null);
  const [highestBid, setHighestBid] = useState(0);
  const [bidAmount, setBidAmount] = useState(0);

  // Function to fetch all available auctions
  const fetchAuctions = async () => {
    const auctionContract = new ethers.Contract(
      auctionContractAddress,
      contractABI,
      provider
    );

    const auctionsCount = await auctionContract.getAuctionsCount();
    const auctions = [];
    for (let i = 0; i < auctionsCount; i++) {
      const auction = await auctionContract.auctions(i);

      const auctionDetails = {
        address: auctionContractAddress,
        nftId: auction.nftId.toString(),
        highestBid: ethers.utils.formatEther(auction.highestBid),
        started: auction.started,
        ended: auction.ended,
        endAt: new Date(auction.endAt * 1000).toLocaleString(),
      };
      auctions.push(auctionDetails);
    }
    setAuctions(auctions);
  };

  // Function to place a bid on the selected auction
  const placeBid = async () => {
    if (selectedAuction) {
      try {
        const auctionContract = new ethers.Contract(
          selectedAuction.address,
          contractABI,
          provider.getSigner()
        );

        const transaction = await auctionContract.bid({
          value: ethers.utils.parseEther(bidAmount),
        });
        await transaction.wait();

        // Bid successfully placed, update the highest bid and refresh auctions
        setHighestBid(ethers.utils.formatEther(bidAmount));
        fetchAuctions();
      } catch (error) {
        console.error("Error placing bid:", error);
      }
    }
  };

  useEffect(() => {
    fetchAuctions();
  }, []);

  return (
    <div>
      <h1>Auction App</h1>
      <div>
        <h2>Available Auctions</h2>
        <ul>
          {auctions.map((auction) => (
            <li key={auction.address}>
              <span>
                Auction ID: {auction.nftId} | Highest Bid: {auction.highestBid}
              </span>
              <button onClick={() => setSelectedAuction(auction)}>
                Select Auction
              </button>
            </li>
          ))}
        </ul>
      </div>
      {selectedAuction && (
        <div>
          <h2>Selected Auction</h2>
          <p>Auction ID: {selectedAuction.nftId}</p>
          <p>Highest Bid: {highestBid}</p>
          <p>End Time: {selectedAuction.endAt}</p>
          <div>
            <h3>Place a Bid</h3>
            <input
              type="number"
              value={bidAmount}
              onChange={(e) => setBidAmount(e.target.value)}
            />
            <button onClick={placeBid}>Place Bid</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AuctionApp;
