// App.js
import React from 'react';
import {Web3} from 'web3';
import { ethers } from 'ethers';
import EnglishAuctionABI from '../artifacts/contracts/Auction.sol/EnglishAuction.json';
import CreateAuction from './CreateAuction';
import ExploreAuctions from './ExploreAuctions';



function App() {
  const contractAddress = "0x437cb27e2407a5f90920ba661d5ed7d8d58c6d38";
  const provider = new ethers.providers.Web3Provider(window.ethereum);

  // Connect to the smart contract
  const signer = provider.getSigner();

  // Debugging statement to check the ABI before creating the contract
  console.log("ABI Type:", typeof EnglishAuctionABI);

  const contract = new ethers.Contract(contractAddress, EnglishAuctionABI, signer);

  return (
    <div className="App">
      <CreateAuction contract={contract} />
      <ExploreAuctions contract={contract} />
    </div>
  );
}

export default App;
